import { CacheStrategy } from './cache-strategy';
import { CachePersistence } from './cache-persistance';
import { RouteExpression } from '../http/route-expression';
export interface CacheDefinition {
    /**
     * The route used to match/identify this cache definition.
     */
    route: RouteExpression;
    /**
     * The caching strategy used to handle this data.
     */
    strategy: CacheStrategy;
    /**
     * The storage mechanism to store the cache in (permanent or temporary).
     */
    storage?: CachePersistence;
    /**
     * Number of seconds until the data become stale and invalid. Once the data
     * expires, the data will not be retrieved from the cache.
     */
    expires?: number;
    /**
     * Explicit identifier for this cached data. This provides a key to store/retrieve
     * the data by. NOTE: When this property is not set, the request URL will be used.
     */
    cacheId?: string;
}
